export const URL_REGEX_FULL = new RegExp(URL_REGEX_HEAD.source + URL_REGEX_TAIL.source, 'i')

const MATCH_URL = new RegExp(`(${URL_REGEX_HEAD.source}${URL_REGEX_TAIL.source})`, 'i')
const MATCH_URL_PREFIX = new RegExp(`.*(${URL_REGEX_HEAD.source})$`, 'i')

export const matchUrlPrefix = (text: string) => {
  return text.match(MATCH_URL_PREFIX)
}

export const matchUrl = (text: string) => {
  return text.match(MATCH_URL)
}

export const matchUrls = (text: string) => {
  const regex = new RegExp(URL_REGEX_FULL.source, 'gi')

  return [...text.matchAll(regex)]
}
